import React, { Component } from 'react'
import { connect } from 'react-redux'
import Map from '../map/map'
import 'tui-grid/dist/tui-grid.css'
import Grid from '@toast-ui/react-grid'
import { bindActionCreators } from 'redux'
import { getDataAsync } from '../../store/modules'

const columns = [
  { name: 'id', title: '지역' },
  { name: 'name', title: '수치' },
  { name: 'etc', title: '단계' }
]

class App extends Component {
  constructor (props) {
    super(props)

    this.state = {}
  }

  getLevel (_num) {
    _num = parseInt(_num, 10)
    return container.reduce((acc, cur) => cur.min <= _num ? cur.level : acc, '')
  }

  render () {
    // const { data } = this.props
    const { gridData } = this.props

    // const gridData = []

    // if (data.geoData) {
    //   data.geoData.forEach(i => {
    //     gridData.push({ id: i.properties.LOC_KOR_NM, name: i.properties.AIR_LV, etc: this.getLevel(i.properties.AIR_LV) })
    //   })
    // }

    return (
      <>
        <Map ncpClientId={process.env.REACT_APP_SERVICEKEY}/>
        <Grid
          data={gridData}
          columns={columns}
          rowHeight={25}
          bodyHeight={100}
          virtualScrolling={true}
          heightResizable={false}
          rowHeaders={['rowNum']}
        />
      </>
    )
  }
}

const mapStateToProps = (state) => ({
  data: state.data
})

const maDispatchToPrope = (dispatch) => ({
  getDataAsync: bindActionCreators(getDataAsync, dispatch)
})

export default connect(mapStateToProps, maDispatchToPrope)(App)
